/// Get calendar database schema SQL
pub fn get_calendar_schema_sql() -> &'static str {
    "CREATE TABLE IF NOT EXISTS calendar_events (id TEXT PRIMARY KEY, title TEXT NOT NULL, description TEXT, event_type TEXT NOT NULL, start_time INTEGER NOT NULL, end_time INTEGER, all_day INTEGER NOT NULL, recurrence_pattern TEXT, created_at INTEGER NOT NULL, updated_at INTEGER NOT NULL, created_by TEXT NOT NULL); CREATE TABLE IF NOT EXISTS event_reminders (id TEXT PRIMARY KEY, event_id TEXT NOT NULL, minutes_before INTEGER NOT NULL, notification_sent INTEGER NOT NULL, FOREIGN KEY (event_id) REFERENCES calendar_events(id) ON DELETE CASCADE); CREATE TABLE IF NOT EXISTS event_attachments (id TEXT PRIMARY KEY, event_id TEXT NOT NULL, attachment_type TEXT NOT NULL, reference_id TEXT NOT NULL, FOREIGN KEY (event_id) REFERENCES calendar_events(id) ON DELETE CASCADE); CREATE INDEX IF NOT EXISTS idx_events_start_time ON calendar_events(start_time); CREATE INDEX IF NOT EXISTS idx_events_created_by ON calendar_events(created_by);"
}
